<?php/* for themes */DEFINE('UMCORE_DIR', get_template_directory() );DEFINE('UMCORE_URL', get_template_directory_uri() );function um_check_umplug() {	include_once( ABSPATH . 'wp-admin/includes/plugin.php' );	if(!is_plugin_active( 'um-plug/um-plug.php' )) {		add_action('admin_notices','um_adminnotes');		add_action('wp_head','um_suggest_umplug');		return false;	} else {		return true;	}}function um_adminnotes() {	echo '<div id="message" class="error">';	echo "<p>The UM-themes requires <a href='". admin_url()."plugin-install.php?tab=search&s=um-plug&plugin-search-input=Search+Plugins'>UM-Plug</a> plugins.</p>";	echo '</div>';}function um_suggest_umplug() { echo "\n\n<!-- UM-PLUG Not Activated -->\n\n"; }$umplug_is = um_check_umplug();/* um-compat ----------------------------------------------------------------------- */if ( ! function_exists('umtag')) {	function umtag($func,$args=array()) {		if (um_getoption('umtag')) {			if (! function_exists($func)) {				$ttdir=get_template_directory()."/template-tags/";				if (file_exists($ttdir.$func.".php")) {					require $ttdir.$func.".php"; call_user_func_array("umtag_".$func,array($args));				} else {					echo "<!-- umtag: $func not exist --->";				}			} else {				call_user_func_array("umtag_".$func,array($args));			}		} else {			echo "<!-- umtag: disable --->";		}	}}if ( ! function_exists('um_getoption')) {	function um_getoption($w,$o="umo") {		$umo->options=get_option($o);		if (isset($umo->options[$w])) {		//if (in_array($w,array_keys($umo->options))) {			return $umo->options[$w];		}	}}